document.addEventListener("DOMContentLoaded",function(){console.log("Dynamic accomodation forms js loaded");tidy_multi_inputs();form=document.getElementById('sim_form_accomodation');});window.addEventListener("click",accomodation_listener);window.addEventListener("input",accomodation_listener);accomodation_prev_el='';function accomodation_listener(event){var el=event.target;form=el.closest('form');var n=el.name;if(n==''||n==undefined){if(el.closest('.nice-select-dropdown')!=null&&el.closest('.inputwrapper')!=null){el.closest('.inputwrapper').querySelectorAll('select').forEach(select=>{if(el.dataset.value==select.value){el=select;n=select.name;}});}else{return;}}
if(el==accomodation_prev_el){return;}
accomodation_prev_el=el;setTimeout(function(){accomodation_prev_el='';},100);if(n=='nextBtn'){nextPrev(1);}else if(n=='prevBtn'){nextPrev(-1);}
process_accomodation_fields(el);}
function process_accomodation_fields(el){var n=el.name;if(n=='childcount'){var v_1=get_field_value('childcount',true,0);if(v_1>0){form.querySelectorAll('[name="ages_of_the_children_label"], [name="childrenage"]').forEach(el=>{try{el.closest('.inputwrapper').classList.remove('hidden');}catch(e){el.classList.remove('hidden');}});}if(v_1==0){form.querySelectorAll('[name="ages_of_the_children_label"], [name="childrenage"]').forEach(el=>{try{el.closest('.inputwrapper').classList.add('hidden');}catch(e){el.classList.add('hidden');}});}}}