var profile_picture = new function(){document.addEventListener('DOMContentLoaded',function(){console.log('Dynamic profile_picture forms js loaded');FormFunctions.tidyMultiInputs();form=document.querySelector('[data-formid="23"]');form.querySelectorAll('select, input, textarea').forEach(el=>profile_picture.p(el));});var prevEl='';var q=function(event){var el=event.target;form=el.closest('form');var n=el.name;if(n==''||n==undefined){if(el.closest('.nice-select-dropdown')!=null&&el.closest('.inputwrapper')!=null){el.closest('.inputwrapper').querySelectorAll('select').forEach(select=>{if(el.dataset.value==select.value){el=select;n=select.name;}});}else{return;}}if(el==prevEl){return;}prevEl=el;setTimeout(function(){prevEl='';},100);if(n=='nextBtn'){FormFunctions.nextPrev(1);}else if(n=='prevBtn'){FormFunctions.nextPrev(-1);}profile_picture.p(el);};window.addEventListener('click',q);window.addEventListener('input',q);this.p=function(el){var n=el.name;if(n=='profile_picture'){var v_1=FormFunctions.getFieldValue('profile_picture',form,true,'hgg',true);if(v_1=='hgg'){form.querySelector('[name="hidden_label"]').closest('.inputwrapper').classList.add('hidden');}}};};

