document.addEventListener("click",(function(e){var c=e.target;if(c.classList.contains("icon")){var r=c.closest(".icon_select_wrapper");if("DIV"==c.tagName)var t=c.querySelector("img").src;else t=c.src;r.querySelector(".icon_url").value=t,r.querySelector(".icon_preview").innerHTML="<img src='"+t+"' class='icon'>",r.querySelector(".dropbtn").textContent="Change Icon"}}));